                        { id: 38, text: "Marine reptiles are known to have a possible life span (great) ______ than that of man.", answer: "greater", type: 'fill_in_the_blank', explanation: "So sánh hơn: 'greater than'." },
                        { id: 39, text: "This one is prettier, but it costs three times (much) ______ as the other one.", answer: "as much", type: 'fill_in_the_blank', explanation: "So sánh bội số: 'three times as much as'." },
                        { id: 40, text: "The (much) ______ we learn, the (much) we forget.", answer: "more / more", type: 'fill_in_the_blank', explanation: "So sánh kép: 'The more..., the more...'" }
